  def part1(s: String) = {
    val f = mutable.Map((0, 0) -> "X")
    def getNextToken(i: Int) =
      ("^[NESW]+".r findFirstIn s.substring(i)).getOrElse(s.substring(i, i + 1))
    def re(i: Int): (Int, Int) = {
      var j = i
      var l = Seq[Int]()
      var a = 0
      while (j < s.length && s(j) != ')') {
        val t = getNextToken(j)
        t match {
          case "^" | "$" => j += 1
          case "|" => l ++= Seq(a); a = 0; j += 1
          case "(" => val x = re(j + 1); a += x._1; j = x._2
          case _ => a += t.length; j += t.length
        }
      }
      l ++= Seq(a)
      val rv = (if (l.min == 0) 0 else l.max, j + 1)
      rv
    }
    re(0)._1
  }

  "part1" should "satisfy the examples given" in {
    assertResult(3)(part1("^WNE$"))
    assertResult(10)(part1("^ENWWW(NEEE|SSE(EE|N))$"))
    assertResult(18)(part1("^ENNWSWW(NEWS|)SSSEEN(WNSE|)EE(SWEN|)NNN$"))
    assertResult(23)(part1("^ESSWWN(E|NNENN(EESS(WNSE|)SSS|WWWSSSSE(SW|NNNE)))$"))
    assertResult(31)(part1("^WSSEESWWWNW(S|NENNEEEENN(ESSSSW(NWSW|SSEN)|WSWWN(E|WWS(E|SS))))$"))
  }

  "part1" should "succeed" in { info(part1(input).toString) }

  def move(d: Char)(p: (Int, Int)) = d match {
    case 'N' => (p._1, p._2 - 1)
    case 'E' => (p._1 + 1, p._2)
    case 'S' => (p._1, p._2 + 1)
    case 'W' => (p._1 - 1, p._2)
  }

  def part2(l: Int, s: String) = {
    val f = mutable.Map((0, 0) -> 0)
    var cnt = 0
    def re(ii: Int, ips: Set[(Int, Int)], in: Int): (Int, Set[(Int, Int)]) = {
      var i = ii
      var ps = ips
      var n = in
      var ts = Set[(Int, Int)]()
      while (i < s.length && s(i) != ')') {
        s(i) match {
          case '^' | '$' =>
            i += 1
          case '|' =>
            ts = ts ++ ps
            ps = ips
            n = in
            i += 1
          case '(' =>
            val e = re(i + 1, ps, n)
            i = e._1
            ps = e._2
          case d: Char =>
            ps = ps.map(move(d)).filter(!f.contains(_))
            ps.foreach { p => f(p) = n; if (n >= l) cnt += 1; n += 1 }
            i += 1
        }
      }
      ts = ts ++ ps
      (i + 1, ts)
    }
    re(0, Set((0, 0)), 1)
    cnt
  }

  "part2" should "satisfy the examples given" in {
    assertResult(3)(part2(1, "^WNE$"))
    assertResult(8)(part2(7, "^ENWWW(NEEE|SSE(EE|N))$"))
    assertResult(15)(part2(9, "^ENNWSWW(NEWS|)SSSEEN(WNSE|)EE(SWEN|)NNN$"))
  }

  "part2" should "succeed" in { info(part2(1000, input).toString) }
